package com.gdgpune.aroundpune;

import java.io.Serializable;
import java.util.List;

/**
 * This is POJO class generated by JsonGen to store response coming from Places
 * API
 * 
 * @author Rohit Lagu
 * @version 1.0
 */
@SuppressWarnings("serial")
public class Results implements Serializable {

	private Geometry geometry;
	/**
	 * Image URL
	 */
	private String icon;
	/**
	 * id for place; this can further use to get detail information
	 */
	private String id;
	/**
	 * Name of place
	 */
	private String name;
	/**
	 * Place's rating, from 0.0 to 5.0, based on user reviews.
	 */
	private Number rating;
	/**
	 * It at any time in future to refresh cached data about this Place
	 */
	private String reference;
	/**
	 * An array of feature types describing the given result.
	 */
	@SuppressWarnings("rawtypes")
	private List types;
	/**
	 * Description of location
	 */
	private String vicinity;

	public Geometry getGeometry() {
		return this.geometry;
	}

	public void setGeometry(Geometry geometry) {
		this.geometry = geometry;
	}

	public String getIcon() {
		return this.icon;
	}

	public void setIcon(String icon) {
		this.icon = icon;
	}

	public String getId() {
		return this.id;
	}

	public void setId(String id) {
		this.id = id;
	}

	public String getName() {
		return this.name;
	}

	public void setName(String name) {
		this.name = name;
	}

	public Number getRating() {
		return this.rating;
	}

	public void setRating(Number rating) {
		this.rating = rating;
	}

	public String getReference() {
		return this.reference;
	}

	public void setReference(String reference) {
		this.reference = reference;
	}

	@SuppressWarnings("rawtypes")
	public List getTypes() {
		return this.types;
	}

	@SuppressWarnings("rawtypes")
	public void setTypes(List types) {
		this.types = types;
	}

	public String getVicinity() {
		return this.vicinity;
	}

	public void setVicinity(String vicinity) {
		this.vicinity = vicinity;
	}

	@Override
	public String toString() {
		return "Name : " + name + " vicinity: " + vicinity + "lat "
				+ geometry.getLocation().getLat();
	}
}
